<?phpif ($act=='save_package'){	$id=cp('id');	$title=cp('title');	$chips=cp('chips');	$price=cp('price');		$chipEx=$db->getFunction("select count(*) from prices where chips='$chips' and id<>'$id'");	if ($chipEx=='0'){		$db->query("update prices set 						title='$title' ,						chips='$chips' ,						price='$price'					where id='$id'");		$return='<div class="ok_div">Saved !</div>';	}else{		$return='<div class="error_div">This package already exist !</div>';	}			$data["object"]=$act.'_feedback';	$data["command"]='set_object_html';	$data["data"]=$return;	$ajax->add($data);										unset($data); // clear data		$data["object"]=$act.'_feedback_'.$id;	$data["command"]='set_object_html';	$data["data"]='Save';	$ajax->add($data);										unset($data); // clear data	}//if ($act=='add_package'){	$title=cp('title');	$chips=cp('chips');	$price=cp('price');		if ($chips==0){		$return='<div class="error_div">Please enter chips value !</div>';	}else{		$chipEx=$db->getFunction("select count(*) from prices where chips='$chips'");		if ($chipEx=='0'){			$db->query("insert into prices set 							title='$title' ,							chips='$chips' ,							price='$price'");			$return='<div class="ok_div">Saved !</div>';						$data["command"]='reload';			$ajax->add($data);												unset($data); // clear data					}else{			$return='<div class="error_div">This package already exist !</div>';		}	}			$data["object"]=$act.'_feedback';	$data["command"]='set_object_html';	$data["data"]=$return;	$ajax->add($data);										unset($data); // clear data		$data["object"]=$act.'_feedback_btn';	$data["command"]='set_object_html';	$data["data"]='Add New Package';	$ajax->add($data);										unset($data); // clear data	}//?>