<?phpclass mysql implements IDatabase{		private $server='localhost';	private $db='pokerize';	private $user='root';	private $pass='';		private $pers=false;	private $connected=false;		function __construct($server,$db,$user,$pass,$connect=false,$pers=false,$type=''){		$this->server=$server;		$this->db=$db;		$this->user=$user;		$this->pass=$pass;		$this->pers=$pers;		 		if ($connect){			$this->connect();		}	}// end constuctor	// -------------- \\// --- Public --- \\// -------------- \\	/* Connect to database	input : 	void	output : 	void	*/	public function connect(){ //connrct to database		if ($this->connected==false){			if ($this->pers){				$link=mysql_pconnect($this->server,$this->user,$this->pass);			}else{				$link =mysql_connect($this->server,$this->user,$this->pass);			}			mysql_select_db($this->db,$link);			mysql_query("set names 'utf8'"); // for multi language			$this->connected=true;		}//	}	/* Execute a query	input : 	string $q	output : 	void	*/	public function query($q){ //		$this->connect();	//connect if not connected 		return @mysql_query($q);	}		/* 2d-array of table rows with colum name as indexies	input : 	string $q	output : 	array ()	*/	public function getRows($q){ // get array of rows objects		$this->connect();	//connect if not connected 		$query=mysql_query($q);		$result=array();		while ($data=@mysql_fetch_object($query)){			$result[]=$data;		}		return $result;	}		public function getRow($q){ // get array of rows objects		$this->connect();	//connect if not connected 		$query=mysql_query($q);		$result=array();		$data=mysql_fetch_object($query);					return $data;	}		/* return new array of 'INSERT' query	input : 	void	output : 	int $ID	*/	public function newId(){ // get array of rows objects		return mysql_insert_id();	}	/* return the function value (sum/count/min) of a query	input : 	string query	output : 	mixed	*/	public function getFunction($q){		$this->connect();	//connect if not connected 		$res=mysql_fetch_array(mysql_query($q));		return $res["0"];	}		}//end class?>